import './Promociones.css'
import logo from '../BlackWings.png';
import React from "react";

export function Promociones() {
    return (
      <div className="App">
        <header className="App-header">
          <p>Promociones</p>
          <div className="Card-2">
          <Cart />
          <Cart />
          <Cart />
          </div>
          <Busqueda />
        </header>
      </div>
    );
  }


const Cart = () =>{
  return (
  <div class="card">
    <img src={logo} className="App-logo" alt="logo"/>
    <h4>Producto</h4>
    <p class="price">$19.99</p>
    <p>Descipción, del producto con el detallado de ingredientes</p>
    <p><button>Agregar al carro</button></p>
  </div>
  )
}

const Busqueda = () =>{
  return(
    <div>
      <fieldset>
        <legend>Sección de búsquedas</legend>
        <label for="idSearch">Buscador</label>
        <input id="idSearch" type="search" name="nmSearch" placeholder="Busqueda patrón: [A-z]{2}[0-9]{4})" aria-label="Buscar a través del contenido del sitio" list="idDataListAsociada" minlength="" maxlength="" pattern="[A-z]{2}[0-9]{4}" size="30"></input>
        <button>Buscar</button>
      </fieldset>
    </div>
  )
}

  export default Promociones;
